var createModal = function(modal) {
var $sbbop = $(&quot;#sbbop-container&quot;),
$sbbopContent = $sbbop.find(&quot;#sbbop-popover-content&quot;),
$sbbopHeader = $container.find(&quot;#sbbop-popover-header&quot;),
$sbbopFooter = $sbbop.find(&quot;#sbbop-popover-footer&quot;);
