sbbopWidth = parseInt(($sbbop.find(&quot;#sbbop-popover&quot;)).attr(&quot;popover-width&quot;), 10);
return modal.create($sbbop, {
&quot;inlineContent&quot;: $sbbopContent.html(),
&quot;header&quot;: $sbbopHeader.html(),
&quot;footer&quot;: $sbbopFooter.html(),
&quot;width&quot;: sbbopWidth,
&quot;activate&quot;: &quot;onclick&quot;,
&quot;name&quot;: &quot;sbbop_modal&quot;
});
}
return undefined;
}
