if(typeof useImageBlockLeftColCentering !== &quot;undefined&quot; &amp;&amp; useImageBlockLeftColCentering){
mainImgContainer.style.marginRight = rightMargin + &quot;px&quot;;
}
mainImgContainer.style.height = containerHeight + &quot;px&quot;;
