function prefetchTYPAssets() {
    var imageAssets = new Array();
    var jsCssAssets = new Array();
      imageAssets.push(&quot;https://images-na.ssl-images-amazon.com/images/G/01/x-locale/common/buy-buttons/review-1-click-order._CB485945748_.gif&quot;);
      imageAssets.push(&quot;https://images-na.ssl-images-amazon.com/images/G/01/x-locale/common/buttons/continue-shopping._CB485936085_.gif&quot;);
      imageAssets.push(&quot;https://images-na.ssl-images-amazon.com/images/G/01/x-locale/common/buy-buttons/thank-you-elbow._CB485935881_.gif&quot;);
      imageAssets.push(&quot;https://images-na.ssl-images-amazon.com/images/G/01/x-locale/communities/social/snwicons_v2._CB485949224_.png&quot;);
      imageAssets.push(&quot;https://images-na.ssl-images-amazon.com/images/G/01/checkout/assets/carrot._CB485936886_.gif&quot;);
      imageAssets.push(&quot;https://images-na.ssl-images-amazon.com/images/G/01/checkout/thank-you-page/assets/yellow-rounded-corner-sprite._CB485934148_.gif&quot;);
      imageAssets.push(&quot;https://images-na.ssl-images-amazon.com/images/G/01/checkout/thank-you-page/assets/white-rounded-corner-sprite._CB485935362_.gif&quot;);
      imageAssets.push(&quot;https://images-na.ssl-images-amazon.com/images/G/01/gno/sprites/nav-sprite-global_bluebeacon-1x_optimized_layout1._CB468670774_.png&quot;);
      imageAssets.push(&quot;https://images-na.ssl-images-amazon.com/images/G/01/x-locale/common/transparent-pixel._CB485935036_.gif&quot;);

    // pre-fetching image assets
    for (var i=0; i&lt;imageAssets.length; i++) {
       new Image().src = imageAssets[i];
    }
    // pre-fetching css and js assets based on different browser types
    var isIE = /*@cc_on!@*/0;
    var isFireFox = /Firefox/.test(navigator.userAgent);
    if (isIE) {
      for (var i=0; i&lt;jsCssAssets.length; i++) {
        new Image().src = jsCssAssets[i];
      }
    }
    else if (isFireFox) {
      for (var i=0; i&lt;jsCssAssets.length; i++) {
        var o =  document.createElement(&quot;object&quot;);
        o.data = jsCssAssets[i];
        o.width = o.height = 0;
        document.body.appendChild(o);
      }
    }
}

    var onload = function () {
        setTimeout(prefetchTYPAssets, 2000);
    };
    if (window.addEventListener) {
        window.addEventListener(&quot;load&quot;, onload);
    } else if (window.attachEvent) { /* for &lt;= IE 8 */
        window.attachEvent(&quot;onload&quot;, onload);
    }

